/////////////////////////////////////////////////////////////////////////////
// This program is responsible for removing (clearing) findings from the
// Coupler.  It works together with the associated masking program.
//
// Returns:  true
//
// Template:  CSG#274
// Version:   6.0
// Created:   08/16/02
// Modified:  02/24/14
/////////////////////////////////////////////////////////////////////////////
{
  // exit true if outside the questionnaire

  if (QstGetNo() < 0)
    exit (true);

  // Finding(s) to be cleared

  short  vnFndLst_FemaleAge10to60[] =
                         {
                         -100,           // Medical history and known conditions
                            0            // list terminator
                         };

  timestamp timAge;     


  // Condition required to leave findings as is

  short  nFnd_female     = MapEntNo (12100,1);

  if (Program ("hp_GetAgeFromBirthDate",0,&timAge) < 0)
   exit (false);

  int nAgeInYears  = TimeGetField(&timAge,0);  

  short  bCond =  Fnd[nFnd_female] == FndP && nAgeInYears > 9 && nAgeInYears < 60;  


  // Check the finding conditions, if they are not met, clear the findings

  if (bCond == false) 
    FndSetList (vnFndLst_FemaleAge10to60,FndA);

  exit (true);
}

