/////////////////////////////////////////////////////////////////////////////
// This clearing program assesses individually all findings whose display 
// is controlled by extension data (rather than a couplet entry program).
// If their condition to display is not met, this program clears them 
// from the Coupler session.
//
// Returns:  true
//
// Template:  CSG#95
// Version:   1.0
// Created:   03/27/13
// Modified:  09/16/13
/////////////////////////////////////////////////////////////////////////////
{

  // exit true if outside the questionnaire

  if (QstGetNo() < 0)
    exit (true);


  // define the display condition

  short  nFnd_Female = MapEntNo ( 12100,1);


  // if any GEN does not map, clearing will not occur, exit false

  if (MapEntNo (  51747,1) < 0 )        // headache around time of menstrual period

    exit (false);  


  // define the finding array size

  short vnFndList_Female[2];    


  // define the individual findings to assess

  vnFndList_Female[0] = MapEntNo (51747,1);     // headache around time of menstrual period
  vnFndList_Female[1] = 0;                     // list terminator


  // if the display condition is absent, set all individual findings absent

  if ( Fnd[nFnd_Female] == FndA )
    FndSetList (vnFndList_Female,FndA);

  // exit

  exit(true);

}

