////////////////////////////////////////////////////////////////////////////////
//          This is a STANDARD PROXY, it should not be edited. 
//          --------------------------------------------------
// This proxy tests if either of two possible pregnancy related findings are 
// present within the Coupler. 
//
// This proxy is sourced by the proxy template Coupler. If both findings
// do not exist in your Coupler, request a customized version of this proxy from
// the Couplet Services Group.
//
// Uncertain responses are handled as absent.
//
// Template:  PXY#24
// Version:   1.2
// Created:   08/20/04
// Modified:  05/17/05
////////////////////////////////////////////////////////////////////////////////
{
  // define the variables

  int nResult = true;
  int nFnd;

  // test if the findings exist and if they are present, if at least one
  // finding is found present, break from the test loop and vote true

  do
  {
    nFnd = MapEntNo (23966,1);          // possible pregnancy
    if (nFnd > 0 && Fnd[nFnd] == FndP)
      break;

    nFnd = MapEntNo (25051,1);          // currently pregnant
    if (nFnd > 0 && Fnd[nFnd] == FndP)
      break;

    // no present findings have been found

    nResult = false;

  } while (false);

  // exit based on nResult

  exit (nResult);
}

