///////////////////////////////////////////////////////////////////////////////
// This program ensures that the pulse rate finding (GEN 52845) is valid as
// the user exits question panel.
//
//     pulse rate MUST be between 15 and 250
//     pulse rate should be above 40 and less than 150
//
// Returns:
//   true:  all tests pass, question flow continues
//   false: any test fails, error message is displayed
//
// Template:  VS#11
// Version:   3.2
// Created:   01/20/04
// Modified:  08/15/07
///////////////////////////////////////////////////////////////////////////////
{
  // confirm the pulse is valid (between 15 and 250)

  if (program ("val_Pulse Rate",0) == false)
    exit (false);

  // Warn the user if the pulse rate is outside "expected" values
  // (pulse rate <= 40 or >= 150)

  short     nFnd_Pulse  = MapEntNo (52845,1);  
  double    fPulse = 0;
  FndValue  fValue;

  FvalGet(nFnd_Pulse, &fValue);
  FvalGetValue (&fValue,&fPulse);

  // define the ranges for "commonly expected values"

  short bPulseUnCommon = fPulse <= 40 || fPulse >= 150;

  // exit true if the pulse is within the commonly expected range
  // otherwise notify the user the pulse should be rechecked.

  if (bPulseUnCommon == true)
  {
    SetErrorCtxt ("The pulse rate is outside the expected range and should be rechecked. If it was correctly measured and entered, it may signal an emergency situation. Do you wish to correct your response?",-1,1);
    exit (false);
  }
  else
    exit (true);
}

