////////////////////////////////////////////////////////////////////////////////
// This program sets the 'high blood pressure (systolic >= 140 or diastolic >= 90)' 
// synthesized finding when the blood pressure is within the described range.
//
//  Returns:
//     true:  sets finding present
//     false: error, sets finding absent
//
// Template:  VS#25
// Version:   1.0
// Created:   05/07/13
// Modified:  05/07/13
////////////////////////////////////////////////////////////////////////////////
{

  // Define the variables

  short    nFnd_SynthFnd  = MapEntNo ( 51079,1);;  
  short    nFnd_ValuedFnd = MapEntNo ( 50885,1);

  // exit false if the findings do not map

  if (nFnd_SynthFnd < 0 || nFnd_ValuedFnd < 0)
    exit (false);         


  // exit true if outside the questionnaire

  if (QstGetNo() < 0)
  {
    if (Fnd[nFnd_SynthFnd] == FndP)
      exit (true);
    else
      exit (false);
  }

  // confirm the blood pressure is valid - if not, set the synth fnd absent and exit false

  if (program ("val_Blood Pressure",0) == false) {
    Fnd[nFnd_SynthFnd] = FndA;
    exit (false);
  } 
  else {

    // retrieve the BP values

    double   fSysBp  = 0;
    double   fDiaBp  = 0;

    fSysBp = program ("hp_BloodPressure",0,0,50885);
    fDiaBp = program ("hp_BloodPressure",0,1,50885);


    // evaluate the valued finding

    short bResult = fSysBp >= 140 || fDiaBp >= 90;


    // if the value is within range, set the synthesized finding present;
    // otherwise, set it absent

    if (bResult == true)
      Fnd[nFnd_SynthFnd] = FndP;
    else
      Fnd[nFnd_SynthFnd] = FndA;
  }
  exit (true);
}

//////////////////////////////////////////////////////////////////////////////////
// This program ensures that the finding, blood pressure (GEN 50885) is valid as
// the user exits the question panel.
//
//     systolic must NOT be less than or equal to diastolic
//     systolic MUST be between 40 and 300
//     diastolic MUST be between 15 and 200
//     
//     systolic should be above 80 and less than 200
//     diastolic should be above 40 and less than 150
//
// Returns:
//   true:  all tests pass, question flow continues
//   false: any test fails, error message is displayed
//
// Template:  VS#8
// Version:   3.7
// Created:   12/29/03
// Modified:  06/11/13
///////////////////////////////////////////////////////////////////////////////
{
  // confirm the blood pressure is valid (sys between 40 & 300, dia between 15 & 200)

  if (program ("val_Blood Pressure",0) == false)
    exit (false);

  // call associated synthesizing programs

  Program ("SetSynthBPRange",0);
  Program ("SetSynthHighBP",0);


  // Warn the user if the blood pressure is outside "expected" values
  // (sys between 80 & 200, dia between 40 & 150)

  short     nFnd_BP       = MapEntNo (50885,1);  
  double    fBPSystolic   = 0;
  double    fBPDiastolic  = 0;
  FndValue  fValue;

  FvalGet(nFnd_BP, &fValue);
  FvalGetValue (&fValue,&fBPSystolic,0);
  FvalGetValue (&fValue,&fBPDiastolic,1);

  // define the ranges for "commonly expected values"

  short bSysBpUnCommon = fBPSystolic <= 80 || fBPSystolic >= 200;
  short bDiaBpUnCommon = fBPDiastolic <= 40 || fBPDiastolic >= 150;

  // exit true if the blood pressure is within the commonly expected range
  // otherwise notify the user the blood pressure should be rechecked.

  if (bSysBpUnCommon == true || bDiaBpUnCommon == true)
  {
    SetErrorCtxt ("The blood pressure is outside the expected range and should be rechecked. If it was correctly measured and entered, it may signal an emergency situation. Do you wish to correct your response?",-1,1);
    exit (false);
  }
  else
    exit (true);
}

///////////////////////////////////////////////////////////////////////////////
// This program validates that the blood pressure finding (GEN 50885) exists
// and tests whether the value entered is valid.
//
//     systolic can not be less than or equal to diastolic
//     systolic must be between 40 and 300
//     diastolic must be between 15 and 200
//
// Returns
//    true:  test(s) passes, question flow continues
//    false: test failed, error message displayed
//
// Customizing instructions:
//  1) Replace the number "1" below (short nQstNum = 1) with the 
//     number of the Blood Pressure question in your Coupler.
{
    short nQstNum = 62;
//   
// Template:  VS#7
// Version:   4.0
// Created:   01/16/04
// Modified:  01/22/13
///////////////////////////////////////////////////////////////////////////////

// define the question number and name

  char    szQstTitle[]    = "BLOOD PRESSURE";

  // define the valued finding(s) and the associated range

  short   nFnd_BP         = MapEntNo (50885,1);  

  double  fBPSystolic     = 0;
  short   nSysMinRange    = 40;
  short   nSysMaxRange    = 300;
  char    szSysMinRange[] = "40";
  char    szSysMaxRange[] = "300";
  char    szSysFndName[]  = "SYSTOLIC BLOOD PRESSURE";

  double  fBPDiastolic    = 0;
  short   nDiaMinRange    = 15;
  short   nDiaMaxRange    = 200;
  char    szDiaMinRange[] = "15";
  char    szDiaMaxRange[] = "200";
  char    szDiaFndName[]  = "DIASTOLIC BLOOD PRESSURE";

  FndValue  fValue;


  // start all test by assuming there is an error, set bStatus false

  short bStatus = false;
  do
  {
    // define questionnaire error message

    char   szErrTxt[200] = "";

    // test if the BP finding mapped from the GEN correctly

      if (nFnd_BP < 0)
      break;

    // test if the finding has a value

    if (FvalHasValue (nFnd_BP))
    {
      FvalGet(nFnd_BP, &fValue);
      FvalGetValue (&fValue,&fBPSystolic,0);
      FvalGetValue (&fValue,&fBPDiastolic,1);

      // check if the value is valid

      short bSysLTOrEqual = fBPSystolic <= fBPDiastolic;
      short bSystolicOut  = fBPSystolic < nSysMinRange || fBPSystolic > nSysMaxRange;
      short bDiastolicOut = fBPDiastolic < nDiaMinRange || fBPDiastolic > nDiaMaxRange;
    }

    // if there is no value or it is invalid, error

    if (!FvalHasValue (nFnd_BP) || bSysLTOrEqual || bSystolicOut || bDiastolicOut)
      break;

    // no errors were found, set bStatus true

    bStatus = true;
  }
  while (false);

  // define the error message for the finding error summary screen

  if (bStatus == false && QstGetNo () < 0)
  {
    Strcpy (szErrTxt,"Invalid response to ");
    Strcat (szErrTxt,szQstTitle);
    SetErrorCtxt(szErrTxt,nQstNum);
  } 

  else if (bStatus == false && bSysLTOrEqual == true)
  {
    Strcpy (szErrTxt,"Please provide an entry for ");
    Strcat (szErrTxt,szSysFndName);
    Strcat (szErrTxt," that is greater than the ");
    Strcat (szErrTxt,szDiaFndName);
    Strcat (szErrTxt,".");
    SetErrorCtxt(szErrTxt,0);
  }

  else if (bStatus == false && bSystolicOut == true)
  {
    Strcpy (szErrTxt,"Please provide an entry for ");
    Strcat (szErrTxt,szSysFndName);
    Strcat (szErrTxt," between ");
    Strcat (szErrTxt,szSysMinRange);
    Strcat (szErrTxt," and ");
    Strcat (szErrTxt,szSysMaxRange);
    Strcat (szErrTxt,".");
    SetErrorCtxt(szErrTxt,0);
  }

  else if (bStatus == false && bDiastolicOut == true)
  {
    Strcpy (szErrTxt,"Please provide an entry for ");
    Strcat (szErrTxt,szDiaFndName);
    Strcat (szErrTxt," between ");
    Strcat (szErrTxt,szDiaMinRange);
    Strcat (szErrTxt," and ");
    Strcat (szErrTxt,szDiaMaxRange);
    Strcat (szErrTxt,".");
    SetErrorCtxt(szErrTxt,0);
  }

  else if (bStatus == false && !FvalHasValue (nFnd_BP))
  {
    Strcpy (szErrTxt,"Please provide an entry for ");
    Strcat (szErrTxt,szQstTitle);
    Strcat (szErrTxt,".");
    SetErrorCtxt(szErrTxt,0);

  }
  // exit with value of bStatus

  exit (bStatus);
}

//////////////////////////////////////////////////////////////////////////////////
// This helper program reads the blood pressure finding using the GEN#50885. It
// receives a parameter (nItem) to define which Bp value to return. If no Bp
// value exists it will return a -1.
//
// Parameters:
//    nItem - 0 = Systolic
//            1 = Diastolic
//
//    nGEN  - The global entity number of the blood pressure valued finding. 
//            (The standard Bp is 50885. However, the hypertension couplers work
//             with a benchmark Bp which is 54213.)
//
// Returns:
//     a floating point value, either the systolic or the diastolic value or
//     a -1, to indicate an error
//
// Template:  VS#6
// Version:   2.0
// Created:   05/07/02
// Modified:  07/09/04
////////////////////////////////////////////////////////////////////////////////

@hp_BloodPressure(int nItem, int nGEN)
{
  // declare the variables

  int    nStatus  = -1;
  short  nFnd_Bp  = MapEntNo (nGEN,1);  

  // begin a code loop

  do
  {
    // test if the GEN mapped, if invalid, break from the code loop

    if (nFnd_Bp < 0)
      break;

    // test blood pressure fnd for a value, if none, break from the code loop

    if (!FvalHasValue (nFnd_Bp))
      break;

    // test if blood pressure value is positive, if neg, break from the code loop

    Fndvalue Fvalue;
    FvalGet (nFnd_Bp,&Fvalue);

    double fBp;
    FvalGetValue (&Fvalue,&fBp,nItem);

    if (fBp < 0)
      break;

    // if all test pass, set nStatus to zero

    nStatus = 0;
  }
  while (false);

  // evaluate status then exit

  if (nStatus < 0)
    exit (-1);
  else  
    exit (fBp);
}

