///////////////////////////////////////////////////////////////////////////////
// This program validates that the pulse finding (GEN 52845) exists
// and tests whether the value entered is valid.
//
//     pulse rate must be between 15 and 250
//
// Returns
//    true:  test(s) passes, question flow continues
//    false: test failed, error message displayed
//
// Customizing instructions:
//  1) Replace the number "2" below (short nQstNum = 2) with the 
//     number of the Pulse question in your Coupler.
{
    short nQstNum = 22;
//   
// Template:  VS#10
// Version:   3.1
// Created:   01/16/04
// Modified:  08/15/07
///////////////////////////////////////////////////////////////////////////////

// define the question number and name

  char    szQstTitle[]    = "PULSE RATE";

  // define the valued finding(s) and the associated range

  short   nFnd_Pulse  = MapEntNo (52845,1);  

  double  fPulse       = 0;
  short   nMinRange    = 15;
  short   nMaxRange    = 250;
  char    szMinRange[] = "15";
  char    szMaxRange[] = "250";
  char    szFndName[]  = "PULSE RATE";

  FndValue  fValue;


  // start all test by assuming there is an error, set bStatus false

  short bStatus = false;
  do
  {
    // define questionnaire error message

    char   szErrTxt[200] = "";
    Strcat (szErrTxt,"Please provide an entry for ");
    Strcat (szErrTxt,szFndName);
    Strcat (szErrTxt," between ");
    Strcat (szErrTxt,szMinRange);
    Strcat (szErrTxt," and ");
    Strcat (szErrTxt,szMaxRange);
    Strcat (szErrTxt,".");
    SetErrorCtxt(szErrTxt,0);

    // test if the finding mapped from the GEN correctly

      if (nFnd_Pulse < 0)
      break;

    // test if the finding has a value

    if (FvalHasValue (nFnd_Pulse))
    {
      FvalGet(nFnd_Pulse, &fValue);
      FvalGetValue (&fValue,&fPulse);

      // check if the value is valid

      short bPulseOut  = fPulse < nMinRange || fPulse > nMaxRange;
    }

    // if there is no value or it is invalid, error

    if (!FvalHasValue (nFnd_Pulse) || bPulseOut)
      break;

    // no errors were found, set bStatus true

    bStatus = true;
  }
  while (false);

  // define the error message for the finding error summary screen

  if (bStatus == false && QstGetNo () < 0)
  {
    Strcpy (szErrTxt,"Invalid response to ");
    Strcat (szErrTxt,szQstTitle);
    SetErrorCtxt(szErrTxt,nQstNum);
  } 

  // exit with value of bStatus

  exit (bStatus);
}

