@FvalCvtConcUnits (fndvalue FndVal, long lEntNo)
{
  double fCvtFactor = -1;
  char   szUnitsConven[21] = "mg/dL";
  char   szUnitsSysInt[21] = "mmol/L";
  char   szUnits[21];

  if (FvalGetUnits (&FndVal,szUnits) == 0)
    exit (-1);

  // Check for Entities that have no molecular conversion

  if (lEntNo == 18363)                              // hemoglobin
    exit (-1);

  // Set the conversion factors.

  if      (lEntNo == 46731)  fCvtFactor =  0.0555;  // glucose, serum (mg/dL to mmol/L)
  else if (lEntNo == 22632)  fCvtFactor =  0.0259;  // cholesterol level, total (mg/dL to mmol/L)
  else if (lEntNo == 05409)  fCvtFactor =  0.0259;  // cholesterol level, HDL (mg/dL to mmol/L)
  else if (lEntNo == 14017)  fCvtFactor =  0.0259;  // cholesterol level, LDL (mg/dL to mmol/L)
  else if (lEntNo == 07253)  fCvtFactor =  0.0113;  // triglycerides (mg/dL to mmol/L)
  else if (lEntNo == 10368)  fCvtFactor =  0.0357;  // Lp(a) (mg/dL to umol/L)
  else if (lEntNo == 31475)  fCvtFactor = 88.4;     // creatinine level (mg/dL to umol/L)
  else if (lEntNo == 13594)  fCvtFactor =  0.357;   // blood urea nitrogen (mg/dL to mmol/L)
  else if (lEntNo == 13592)  fCvtFactor =  1.0;     // sodium (mEq/L to mmol/L)
  else if (lEntNo == 18865)                         // Lead, (ug/dL to umol/L) 
  {
    fCvtFactor =  0.0483;
    char szTemp[21] = "ug/dL";
    strcpy (szUnitsConven,szTemp);
  }
  else
    exit (-1);

  // Determine the class or system of the current units.

  char szRegExp[] = "^((mmol/L)|(umol/L)|(nmol/L)|(pmol/L))$";
  char bSIUnits;

  bSIUnits = (Strrex (szUnits,szRegExp,0,0,true) >= 0);

  // Set the source and target units for special cases.

  if (lEntNo == 10368 || lEntNo == 31475)  // creatinine level or Lp(a)?
    Strcpy (szUnitsSysInt,"umol/L");       // use micromoles per liter

  // Perform the unit conversion.

  if (!bSIUnits)
    FvalCvtUnits (&FndVal,szUnitsConven);
  else
  {
    double fValue;

    FvalCvtUnits (&FndVal,szUnitsSysInt);
    FvalGetValue (&FndVal,&fValue);
    fValue = fValue / fCvtFactor;
    FvalSetUnits (&FndVal,szUnitsConven);
    FvalSetValue (&FndVal,&fValue);
  }

  exit (0);
}

