// This helper program tallies the number of voting evidence or risk factor
// relationships associated with a diagnostic primary option, or the number of
// indication (pro) relationships associated with a management primary option.
//
// Parameters: 
//   vnFndSets[100] - an array with all the primary option relationships within
//                    a Coupler. Every primary option has each of the four
//                    possible relationship types (Evid, Risk, Pro, Con)
//                    separated by zero's.
//
//   vnPOs[50]      - an array with all the primary options within a Coupler,
//                    terminated with a zero.
//
//   vnVotes[4]     - an array to return the vote counts of the 4 relationship
//                    types
//
// Returns:
//   an integer value representing the number of voting evidence/indication
//   relationships, the same value as found in vnVotes[0] or vnVotes[3],
//   or -1, which indicates an error.
//
// Template:  POF#4
// Version:   3.3
// Created:   01/22/04
// Modified:  02/24/04
////////////////////////////////////////////////////////////////////////////////

///////////////////////////////////////////////////////////////////////////
// Program hp_PoRelCount may contain a comment instructing you to replace
// the following label by an updated label. If the label line given in the
// comment differs from the one below then go ahead and perform the
// replacement.
///////////////////////////////////////////////////////////////////////////

@hp_RelCount (short vnFndSets[72],short vnPOs[5],short vnVotes[4])
{
  // Get the current Primary Option record number

  short nPoNum = PrimOptGet (2);

  if (nPoNum <= 0)
    exit (-1);

  // Determine the location of the current PO within the vnPOs array

  short nPoInx = 0;

  while (vnPOs[nPoInx] != nPoNum && vnPOs[nPoInx] != 0)
    nPoInx = nPoInx + 1;

  // If the current PO was not found in the PO array, exit with an error

  if (vnPOs[nPoInx] == 0)
    exit (-1);

  // Locate the appropriate relationship records for the current PO

  short nRelInx  = 0;
  short nZeroCnt = 0;

  while (nZeroCnt != nPoInx * 4)
  {
    if (vnFndSets[nRelInx] == 0)
      nZeroCnt = nZeroCnt + 1;

    nRelInx = nRelInx + 1;
  }

  // Evaluate the findings and/or proxies in the relationship array (vnFndSets)

  short nTypeIx = 0;
  short nRelCnt = 0;
  short nRecNum = vnFndSets[nRelInx];
  short bResult = false;

  while (nTypeIx < 4)
  {
    while (nRecNum != 0)
    {
      if (nRecNum < 0)
        bResult = Program (-nRecNum,0);
      else
        bResult = (Fnd[nRecNum] == FndP);

      if (bResult)
        nRelCnt = nRelCnt + 1;

      nRelInx = nRelInx + 1;      
      nRecNum = vnFndSets[nRelInx];
    }

    // Record the number of voting relationships and clear the count

    vnVotes[nTypeIx] = nRelCnt;
    nRelCnt = 0;

    // Increment the indexes and reload the current record or proxy number

    nTypeIx = nTypeIx + 1;
    nRelInx = nRelInx + 1;      
    nRecNum = vnFndSets[nRelInx];
  }      

  // get the current primary option relationship type,
  // return the number of voting 'evidence' or 'pro' relationships

  short nPORelType = PrimOptGet (3);

  if      (nPORelType == RelEvi || nPORelType == RelRsk)
    exit (vnVotes[0]);            // number of findings: voting 'evidence'
  else if (nPORelType == RelPro || nPORelType == RelCon)
    exit (vnVotes[2]);            // number of findings: voting 'pros'
  else
    exit (-1);
}

