////////////////////////////////////////////////////////////////////////////////
// This program computes the age from the date of birth.
//
// Parameters:
//   timeAge receives the age as { years, months (0-12), days (0-31)}
//
// Returns:
//    true:   passed, set age to a valid value, sets finding present
//    false:  error,  sets finding absent
//
// Notes:
//   If a valid age can't be obtained from hp_GetAgeFromBirthDate then the age
//   finding is set absent. (The value is cleared as a result.)
// 
// Template:  GEN#5
// Version:   5.1
// Created:   07/17/02
// Modified:  08/14/07
////////////////////////////////////////////////////////////////////////////////
{

  // map the age finding

  int nFnd_Age = MapEntNo (26479,1);

  // exit false if the finding does not map

  if (nFnd_Age < 0)
    exit (false);         


  // exit if outside the questionnaire; do not alter findings

  if (QstGetNo() < 0)
  {
    if (Fnd[nFnd_Age] == FndP)
      exit (true);
    else
      exit (false);
  } 


  // compute age

  timestamp timAge;
  int       nResult;

  nResult = Program ("hp_GetAgeFromBirthDate",0,&timAge);

  // exit false if a valid age is not returned, set finding absent

  if (nResult < 0)
  {
    Fnd[nFnd_Age] = FndA;
    exit (false);        
  }

  // set age finding with units appropriate to the calculated age

  fndvalue  Fvalue;
  int       vnAgeComps[3];
  int       nAge;

  FvalInit (nFnd_Age,&Fvalue);

  vnAgeComps[0] = TimeGetField (&timAge,0);  // years
  vnAgeComps[1] = TimeGetField (&timAge,1);  // months
  vnAgeComps[2] = TimeGetField (&timAge,2);  // days

  do
  {
    // set units to "years" if age is 2 years or older

    nAge = vnAgeComps[0];

    if (nAge >= 2)
    {
      FvalSetUnits (&Fvalue,"years");
      break;
    }

    // set units to "months" if age is 1 to 23 months

    nAge = 12 * vnAgeComps[0] + vnAgeComps[1];

    if (nAge >= 1)
    {                                   
      FvalSetUnits (&Fvalue,"months");
      break;
    }

    // set units to "days" if age is less than one month 

    nAge = vnAgeComps[2];
    FvalSetUnits (&Fvalue,"days");

  }
  while (false);

  // set the age value and exit

  FvalSetValue (&Fvalue,&nAge);
  FvalSet (nFnd_Age,&Fvalue);
  Fnd[nFnd_Age] = FndP;  

  exit (true); 
}

