///////////////////////////////////////////////////////////////////////////////
// This program validates that a finding "male" (GEN 3066) exists, that a
// finding "female" (GEN 12100) exists and that a finding "date of birth"
// (GEN 31314) also exists.  It then tests that only one gender finding is
// present, treating uncertain responses as 'absent' and then as 'present'.
// It then tests that a valid date of birth is entered, such that the resulting
// age is within the range described below.
//   
// Returns:
//   true:  all tests pass, question flow continues
//   false: any test fails, question flow is halted
//
// Customizing Instructions:
//   1) Record the number of the Gender/DOB question that starts sequence 1.
//      Replace the "1" in the line below with the correct question number 
//      for your Coupler.
{
  short nGenderDOB  = 2;

//   2) If this Coupler is gender specific, (the DOB only version of the 
//      Gender/DOB question is being used), replace the "false" in the line 
//      below with "true".
//
  short bDOBOnly = false;

//   3) If this Coupler's age range differs from the standard (min age = 0 years
//      and max age = 120 years), provide below the minimum and maximum ages
//      appropriate for running this Coupler.
//
  short nAgeMin        = 0;
  short nAgeMax        = 120;
// 
//
// Template:  GEN#4
// Version:   7.3
// Created:   06/12/02
// Modified:  11/10/09
///////////////////////////////////////////////////////////////////////////////

  // determine current question and next question numbers

  int nSequence      = 0;
  int nCurrent       = QstGetNo ();
  int nNext          = QstGetBranch (0);

  // map the finding numbers from the GENs

  short  nFnd_DOB     = MapEntNo (31314,1);
  short  nFnd_Age     = MapEntNo (26479,1);
  short  nFnd_Male;
  short  nFnd_Female;

  if (!bDOBOnly)
  {
    nFnd_Male   = MapEntNo ( 3066,1);
    nFnd_Female = MapEntNo (12100,1);
  }

  // start all test by assuming there is an error, set bStatus false

  short bStatus = false;
  do
  {
    // test if the findings mapped from the GENs correctly

    if (bDOBOnly == false && (nFnd_Male < 0 || nFnd_Female < 0))
      break;

    if (nFnd_DOB < 0 || nFnd_Age < 0)
      break;


    // test for a valid gender finding

    if (!bDOBOnly)
    {
      short vLst[] = {0,0,0};

      if (nFnd_Male   < 0)  nFnd_Male   = 0;
      if (nFnd_Female < 0)  nFnd_Female = 0;

      vLst[0] = nFnd_Male;
      vLst[1] = nFnd_Female;  

      if (FndTestList (vLst,false) != 1 || FndTestList (vLst,true) != 1)
      {
        program("hp_GenderDobErrMsg",0, "Please select either female or male.");
        break;
      }    
    }

    // check that the DOB finding has a value

    if (!FvalHasValue (nFnd_DOB))
    {
      program ("hp_GenderDobErrMsg",0, "Please provide a valid date of birth.");
      break;
    }

    // declare variables

    fndvalue  fValue;
    timestamp timDOB;
    timestamp timCur;
    timestamp timAge;
    int       nResult;
    int       nAgeYrs;

    // retrieve date of birth

    FvalGet (nFnd_DOB,&fValue);
    FvalGetValue (&fValue,&timDOB);

    // retrieve current date

    TimeGetCur(&timCur);

    // age calculation failed, DOB is in the future

    nResult = program("hp_GetAgeFromBirthDate",0,&timAge);
    if (nResult < 0)
    {
      program("hp_GenderDobErrMsg",0,"You have entered a date of birth outside the range appropriate for this Coupler.");
      break;
    }    

    // is the age below AgeMin or above AgeMax?

    nAgeYrs = TimeGetField(&timAge,0);
    if (nAgeYrs < nAgeMin || nAgeYrs > nAgeMax)
    {
      program("hp_GenderDobErrMsg",0,"You have entered a date of birth outside the range appropriate for this Coupler.");
      break;
    }

    // no errors were found, set bStatus true

    bStatus = true;
  }
  while (false);

  if (bStatus == true)
    QstSetBranch (0,nNext);
  else
  {
    QstSetBranch (0,nSequence);

    if (nCurrent < 0)
      SetErrorCtxt ("Invalid response to GENDER OR DATE OF BIRTH.",nGenderDOB);
  }

  // return value of bStatus

  exit (bStatus);
}

////////////////////////////////////////////////////////////////////////////////
// This helper program tests that the DOB finding exists and has a value. If
// the GEN fails to map to a finding, the finding is not valued or the date of
// birth provided is in the future, the program will return a -1. If the finding
// exists and has a value the program returns the Age.
//
// Arguments:
//   timeAge is a place holder for age in years
//
// Returns:
//   Age, in days to the closest whole number
//   (if -1 is returned, it indicates the program failed in some way)
//
// Customizing Instructions:
//   No Customization is needed.
// 
// Notes:
//   The age is provided as a timestamp value and as the number of days. Either
//   can be used depending on the needs of the calling program.
//
// Template:  GEN#7
// Version:   2.1
// Created:   03/04/02
// Modified:  10/02/09
////////////////////////////////////////////////////////////////////////////////
@hp_GetAgeFromBirthDate (timestamp timAge)
{
  long lFnd_BirthDate = MapEntNo (31314,1);

  // if the finding maps incorrectly or is not valued, return an error

  if (lFnd_BirthDate < 0 || !FvalHasValue (lFnd_BirthDate))
    exit (-1);

  // define the variables

  timestamp timDOB;
  timestamp timCur;
  fndvalue  Fvalue;
  double    fAgeInDays;

  // get the date of birth and the current date

  FvalGet (lFnd_BirthDate,&Fvalue);
  FvalGetValue (&Fvalue,&timDOB);
  TimeGetCur (&timCur);

  // if the date of birth entry is in the future, return an error

  if (Program ("hp_DatePastPresFuture",0,&timDOB,&timCur) > 0)
    exit (-1);

  // set default DOB to valid date to prevent run time from  
  // returning an error when in scan mode

  if (false)   
  {
    TimeInit (&timDOB,1990, 1, 1,0,0,0);  // Jan  1, 1990 (12:00 AM)
  }

  // get the time elapsed and the timespan.  Return the age in days rounded to
  // the nearest whole day.

  TimeGetElapse (&timAge,&timCur,&timDOB);
  TimeGetSpan (&fAgeInDays,&timCur,&timDOB,0);

  exit (fAgeInDays + 0.5);
}

