////////////////////////////////////////////////////////////////////////////////
// This program calculates the Body mass index (GEN 4769) synthesized finding.
// It uses the height (GEN 16645) and the weight (GEN 16647) findings.  It converts
// the units to meters and kilograms to use the simplist conversion calculation.
// It checks that the findings are valued, but it assumes that if they are valued
// they have been correctly validated at entry.
//
// Parameters:
//   none
//
// Returns:
//    true:   passed, set BMI to a valid value, sets finding present
//    false:  error,  BMI set absent if value is < 0 or > 200
//
// Template:  BMI#5
// Version:   10.1
// Created:   04/24/02
// Modified:  08/20/09
////////////////////////////////////////////////////////////////////////////////
{
  // map the findings 

  short    nFnd_Height   = MapEntNo (16645,1);
  short    nFnd_Weight   = MapEntNo (16647,1);
  short    nFnd_BMI      = MapEntNo ( 4769,1);

  // exit false if any finding does not map

  if ((nFnd_BMI < 0) || (nFnd_Height < 0) || (nFnd_Weight < 0))
    exit (false);


  // exit if outside the questionnaire; do not alter findings

  if (QstGetNo() < 0)
  {
    if (Fnd[nFnd_BMI] == FndP)
      exit (true);
    else
      exit (false);
  }    

  // retrieve and validate the height & weight values. Because height & weight are
  // provider data, we can not assume they will be present

  fndvalue  fValue;
  double    fHeight = 0;
  double    fWeight = 0;

  if (FvalHasValue (nFnd_Height))
  {
    FvalGet (nFnd_Height,&fValue);
    FvalCvtUnits(&fValue, "meters");
    FvalGetValue (&fValue,&fHeight);
  }
  else
    fHeight = 0;

  if (FvalHasValue (nFnd_Weight))
  {
    FvalGet (nFnd_Weight,&fValue);
    FvalCvtUnits(&fValue, "kilograms");
    FvalGetValue (&fValue,&fWeight);
  }
  else
    fWeight = 0;

  // calculate BMI if both values are positive numbers

  double  fBMI    = 0;
  int     nBMI    = 0;

  // protect against devide-by-zero error in scan mode

  if (fHeight == 0 || fWeight == 0)
  {
    fHeight = 1;
    fWeight = 0;
  }
  else
  {
    fBMI = fWeight / (fHeight * fHeight);

    // get the 100ths place decimal for rounding

    int     nBMI_100thsMask = 0;            // Mask for 100th place digit
    int     nBMI_100thsPlc  = 0;            // Least Sig Dig is 100ths place decimal 
    int     nBMI_100thsDec  = 0;            // The 100ths place decimal
    double  fRound          = 0;            // rounding factor

    if (fBMI * 10 > 32767)                  // invalid data, will not fit into    
      fBMI = 0;                             // 16 bit variable, set to zero

    nBMI_100thsMask = fBMI * 10;            // Multiply Base value by 10
    nBMI_100thsMask = nBMI_100thsMask * 10; // Multiply result by 10 to strip
                                            // off 100th place integer
    nBMI_100thsPlc  = fBMI * 100;           // Multiply Base value by 100

    nBMI_100thsDec  = nBMI_100thsPlc - nBMI_100thsMask;

    if (nBMI_100thsDec >= 5)
      fRound = 0.1;

    // round the calculated BMI to the 10ths decimal place

    nBMI = fBMI * 10;
    fBMI = nBMI;
    fBMI = fBMI / 10;
    fBMI = fBMI + fRound;
  }

 // confirm the BMI is valid

  if (fBMI <= 0 || fBMI >= 200)
  {
    exit(false); 
  }

  // set the synthesized finding

  FvalInit (nFnd_BMI, &fValue);
  if (FvalSetValue (&fValue, &fBMI) < 0)
    exit (false);
  if (FvalSet (nFnd_BMI, &fValue) < 0)
    exit (false);


  // set the BMI finding present

  Fnd[nFnd_BMI] = FndP;


  exit (true);

}

///////////////////////////////////////////////////////////////////////////////
// This program will scan a list of questions to determine if the current
// question is in that list.
//
// This helper program is used in conjunction with the associated clearing and
// masking programs. 
//
// Returns:
//   true:  the current question is in the list
//   false: the current question is not found in the list
//
// Template:  \\pkc_vt02\Departments\CSG\Projects\Library\MaskingInstructions.txt
// Version:   3.0
// Created:   08/14/02
// Modified:  04/01/03
///////////////////////////////////////////////////////////////////////////////

@hp_InThisQstList (short nCurQst, short vnQstLst[100])
{
  short nIx = 0;

  while (vnQstLst[nIx] != 0)
  {
