///////////////////////////////////////////////////////////////////////////////
// This program validates that a finding height (GEN 16645) exists, that a
// finding weight (GEN 16647) also exists. It then tests that the height and 
// weight findings are within thier defined ranges.
//
// Height must be between 1 - 8 ft (30 - 270 cm).
// Weight must be between 10 - 600 lbs (5 - 275 kg).
//
// Returns:
//   true:  all tests pass, question flow continues
//   false: any test fails, error message is displayed
//
//
// CUSTOMIZING INSTRUCTIONS:
//
//   1) Record the number of the height and weight question.  Replace the "1"
//      in the line below with the correct question number for your Coupler.
{
    short nHgtWgtQstNum = 16;

//   2) If this Coupler is atypical and the height and weight findings are NOT
//      provider specific, replace the "true" in the line below with a "false".
//
    short bHghtWghtProvSpec = false;
//
//
// Template:  BMI#7
// Version:   7.9
// Created:   04/24/02
// Modified:  08/21/07
///////////////////////////////////////////////////////////////////////////////

  // map the findings via their GENs

  short      nFnd_Height = MapEntNo (16645,1);
  short      nFnd_Weight = MapEntNo (16647,1);

  // start all test by assuming there is an error, set bStatus false

  short bStatus = false;
  do
  {
   // test if the findings mapped from the GENs correctly

    if (nFnd_Height < 0 || nFnd_Weight < 0)
      break;    

    // when in the finding error summary and "BOTH" height and weight have
    // no values, assume the question has not been presented yet, no error

    short bNoHeight = !FvalHasValue (nFnd_Height);
    short bNoWeight = !FvalHasValue (nFnd_Weight);

    if (QstGetNo () <= 0 && bHghtWghtProvSpec && bNoHeight && bNoWeight)
    {
      bStatus = true;    // do not report the lack of both findings as an error
      break;
    }


    // determine height units

    fndValue  Fvalue;
    char      szUnits[12];

    double    fHeight;
    short     bHgtMetricUnits = false;

    if (FvalHasValue (nFnd_Height))
    {
      FvalGet (nFnd_Height,&Fvalue);
      FvalGetUnits (&Fvalue,szUnits);

      if (Strequal (szUnits,"centimeters",0))
        bHgtMetricUnits = true;

      else if (Strequal (szUnits,"kilometers",0))
      {
        bHgtMetricUnits = true;
        FvalCvtUnits(&Fvalue, "centimeters");
      }

      else if (Strequal (szUnits,"meters",0))
      {
        bHgtMetricUnits = true;
        FvalCvtUnits(&Fvalue, "centimeters");
      }

      else if (Strequal (szUnits,"millimeters",0))
      {
        bHgtMetricUnits = true;
        FvalCvtUnits(&Fvalue, "centimeters");
      }

      else
      {
        bHgtMetricUnits = false;
        FvalCvtUnits(&Fvalue, "inches");
      }

      // retrieve the height

      FvalGetValue (&Fvalue,&fHeight);

      // negate any unit conversion round-off factors

      long nHeight = fHeight * 100000;
      fHeight = nHeight;
      fHeight = fHeight / 100000;

    }
    else

      fHeight = 0;


    // validate the height

    if (bHgtMetricUnits == true)
    {
      program ("hp_EnhancedErrMsg",0, "Please provide a height between 30 centimeters and 270 centimeters.");
      if (fHeight < 30 || fHeight > 270)
        break;
    }
    else
    {  
      program ("hp_EnhancedErrMsg",0, "Please provide a height between 1 foot and 8 feet.");
      if (fHeight < 12 || fHeight > 96)
        break;
    }


    // determine weight units

    double    fWeight;
    short     bWgtMetricUnits = false;

    if (FvalHasValue (nFnd_Weight))
    {
      FvalGet (nFnd_Weight,&Fvalue);
      FvalGetUnits (&Fvalue,szUnits);

      if (Strequal (szUnits,"kilograms",0))
        bWgtMetricUnits = true;

      else if (Strequal (szUnits,"grams",0))
      {
        bWgtMetricUnits = true;
        FvalCvtUnits(&Fvalue, "kilograms");
      }

      else if (Strequal (szUnits,"milligrams",0))
      {
        bWgtMetricUnits = true;
        FvalCvtUnits(&Fvalue, "kilograms");
      }

      else
      {
        bHgtMetricUnits = false;
        FvalCvtUnits(&Fvalue, "pounds");
      }

      // retrieve the weight

      FvalGetValue (&Fvalue,&fWeight);

      // negate any unit conversion round-off factors

      long nWeight = fWeight * 100000;
      fWeight = nWeight;
      fWeight = fWeight / 100000;
    }
    else

      fWeight = 0;


    // validate the weight

    if (bWgtMetricUnits == true)
    {
      program ("hp_EnhancedErrMsg",0, "Please provide a weight between 5 kilograms and 275 kilograms.");
      if (fWeight < 5 || fWeight > 275)
        break;
    }
    else
    { 
      program ("hp_EnhancedErrMsg",0, "Please provide a weight between 10 pounds and 600 pounds.");
      if (fWeight < 10 || fWeight > 600)
        break;
    }

    // no errors were found, set bStatus true

    bStatus = true;
  }
  while (false);

  // set the error message for the finding error summary

  if (bStatus == false && QstGetNo () <= 0)
    SetErrorCtxt ("Invalid response to HEIGHT OR WEIGHT.", nHgtWgtQstNum);

  // exit with value of bStatus

  exit (bStatus);
}

