////////////////////////////////////////////////////////////////////////////////
// This helper program determines if the given date is past, present or future
// in comparison to the target date.  
//
// Arguments:
//   timGiven  -- date in question
//   timTarget -- reference date, typically the system date
//
// Returns:
//  -1: past     (timGiven is in the past compared to the timTarget)
//   0: present  (timGiven is equal to the timTarget)
//   1: future   (timGiven is in the future compared to the timTarget)
// 
// Template:  GEN#6
// Version:   1.0
// Created:   12/12/01
// Modified:  06/27/02
//////////////////////////////////////////////////////////////////////////////////
@hp_DatePastPresFuture (timestamp timGiven, timestamp timTarget)
{
  short nDiff;
  short nIndex = 0;

  while (nIndex < 3)
  {
    nDiff = TimeGetField (&timGiven,nIndex) -
            TimeGetField (&timTarget,nIndex);

    if (nDiff != 0)
    {
      if (nDiff < 0)  
        exit (-1);                      // timGiven is in the past
      else
        exit (1);                       // timGiven is in the future
    }

    nIndex = nIndex + 1;
  }

  exit (0);                             // date is today
}

